export const addKeyPairRepo = async ({
    clientId,
    userId,
    publicKey,
    privateKey,
    createdAt,
}: Record<string, string>) => {
    const query = `INSERT INTO keyPair (clientId, userId, publicKey, privateKey, createdAt) VALUES (?, ?, ?, ?, ?)`;
