export const CheckBox = ({
    label,
    name,
    helperText,
    value,
    error,
    disabled,
    size,
    required,
    layoutClass,
    options,
    className,
    customField,
    onChange,
}: Pick<
    IFormField,
    | 'label'
    | 'helperText'
    | 'value'
    | 'error'
    | 'disabled'
    | 'size'
    | 'required'
    | 'layoutClass'
    | 'options'
    | 'className'
    | 'customField'
> &
    Required<Pick<IFormField, 'name' | 'onChange'>>) => {
    const handleInput = (option: string) => {
        const _value = option;
        onChange({
            name,
            value: _value === value ? undefined : _value,
        });
    };

    return (
        <div className={`w-full ${className}`}>
            <label
                htmlFor={name}
                className="block mb-1 text-sm font-bold text-gray-600 dark:text-white"
            >
                {label}
                {label && required && (
                    <span v-show="" className="text-red-400 font-bold text-xs">
                        *
                    </span>
                )}
            </label>
            <div className={`flex flex-wrap gap-3 mt-2 ${layoutClass}`}>
                {options?.map((option, index) => (
                    <div
                        v-for="(option, index) in options"
                        key={option.id}
                        className="flex flex-row items-center"
                    >
                        <input
                            type="checkbox"
                            name={name}
                            data-testid={name}
                            checked={value?.includes(option.label)}
                            className={`text-left mr-2 ${size}`}
                            onClick={() => handleInput(option.label)}
                            disabled={disabled}
                        />
                        <span
                            className="text-sm"
                            dangerouslySetInnerHTML={{ __html: option.label }}
                        />
                        {customField && index === options.length - 1 && (
                            <input
                                v-if=""
                                type="text"
                                className="outline-none border-b-[1px] border-b-gray-600 w-full"
                                disabled={
                                    disabled || !value?.includes(option.label)
                                }
                                onChange={(e) => {
                                    if (value?.includes(option.label))
                                        handleInput(
                                            `${e.target.value} ${option.label}`
                                        );
                                }}
                            />
                        )}
                    </div>
                ))}
            </div>
            <p
                data-testid={`${name}-error`}
                className={`mt-1 text-xs italic min-h-4 ${error ? 'text-red-500' : 'text-gray-400'}`}
            >
                {error || helperText}
            </p>
        </div>
    );
};

import { ChangeEvent, DragEvent, useEffect, useState } from 'react';

//
import { DialogView } from '../dialog';
import { IFormField } from './form.types';
import { byteFormat } from 'services/repository/utils';
import { CropperView } from '../cropper';
import SvgIcon from '../svg';

//
export const FileUpload = ({
    name,
    label,
    helperText,
    error,
    disabled,
    max = '1',
    size = 'p-3 text-md',
    required,
    layoutClass,
    className,
    onChange,
    accept = 'image/*',
    multiple,
    cropper = false,
    fileSize = 52428800,
    imageSize = 'h-24 w-24',
    icon,
    value,
}: Pick<
    IFormField,
    | 'label'
    | 'helperText'
    | 'value'
    | 'error'
    | 'disabled'
    | 'max'
    | 'size'
    | 'required'
    | 'layoutClass'
    | 'className'
    | 'onChange'
    | 'accept'
    | 'multiple'
    | 'cropper'
    | 'fileSize'
    | 'imageSize'
    | 'icon'
> &
    Required<Pick<IFormField, 'name' | 'onChange'>>) => {
    const [files, setFiles] = useState<File[]>([]);
    const [localFile, setLocalFile] = useState('');
    const [selectedFile, setSelectedFile] = useState<File>();
    const [showCropper, setCropper] = useState(false);
    const [isDragging, setDrag] = useState(false);
    const [localError, setLocalError] = useState('');

    const handleDrag = () => {
        setDrag((prev) => !prev);
    };

    const onDrop = (event: DragEvent<HTMLLabelElement>) => {
        setDrag(false);
        if (!disabled) {
            event.preventDefault();
            event.stopPropagation();
            loadFile({
                target: { files: event.dataTransfer!.files },
            } as ChangeEvent<HTMLInputElement>);
        }
    };

    const loadFile = async (event: ChangeEvent<HTMLInputElement>) => {
        const files = [];
        setLocalError('');
        const fileInput = event.target;

        if (fileInput.files && fileInput.files.length > 0) {
            if (fileInput.files.length <= +max) {
                for (const file of fileInput.files) {
                    const acceptFileRegex = new RegExp(
                        accept.replace(/,\s?/g, '|')
                    );
                    if (acceptFileRegex.test(file.type || '')) {
                        if (file.size > fileSize)
                            setLocalError(
                                `Upload limit maximum ${byteFormat(fileSize, 0)} allowed`
                            );
                        else {
                            if (cropper) {
                                setLocalFile(getObjectURL(file));
                                setSelectedFile(file);
                                setCropper(true);
                            } else {
                                files.push(file);
                                onChange?.({
                                    name,
                                    value: files,
                                });
                            }
                        }
                    } else {
                        setLocalError(`${file.name} file format is invalid`);
                    }
                }
            } else setLocalError(`Maximum allowed files : ${max}`);
        }
        setFiles(files);
        fileInput.value = '';
    };

    const getObjectURL = (file: File | string) => {
        return typeof file === 'string'
            ? file
            : file.type.includes('image')
              ? URL.createObjectURL(file)
              : '/icons/svg/file.svg';
    };
    const removeLocalImages = (index: number) => {
        setFiles((prev) => {
            const files = prev;

            return files.filter((_, i) => i !== index);
        });
        onChange?.({
            name,
            value: files,
        });
    };

    const toggleCropper = () => {
        setCropper((prev) => !prev);

        if (!showCropper) {
            setLocalFile('');
            setSelectedFile(undefined);
        }
    };

    const callback = (image: Blob) => {
        setCropper(false);
        const file = new File([image], selectedFile!.name, {
            type: 'image/png',
        });
        setSelectedFile(file);
        setFiles([file]);
        onChange?.({
            name,
            value: [file],
        });
    };

    useEffect(() => {
        setFiles(value);
    }, [value]);

    return (
        <div className={`w-full ${className}`}>
            <input
                id={`${name}-browse`}
                data-testid={name}
                name={name}
                type="file"
                accept={accept}
                disabled={disabled}
                multiple={multiple}
                hidden
                onChange={loadFile}
            />
            {label && (
                <label
                    htmlFor={name}
                    className="block mb-1 text-sm text-gray-600 dark:text-white"
                >
                    {label}
                    {label && required && (
                        <span v-show="" className="font-bold text-xs">
                            *
                        </span>
                    )}
                </label>
            )}
            <div
                className={`w-full grid grid-cols-2 gap-1 items-end ${layoutClass}`}
            >
                <label
                    htmlFor={`${name}-browse`}
                    className={`flex flex-col gap-1 items-center justify-center rounded-lg border-gray-300 border border-dashed cursor-pointer h-full ${size} ${
                        disabled
                            ? 'shadow-[_0px_0px_20px_inset] shadow-gray-400'
                            : isDragging
                              ? ' shadow-[_0px_0px_20px_inset] shadow-green-500'
                              : ''
                    }`}
                    onDragOver={(e) => {
                        e.preventDefault();
                        e.stopPropagation();
                        handleDrag();
                    }}
                    onDragLeave={handleDrag}
                    onDrop={(e) => {
                        e.preventDefault();
                        e.stopPropagation();
                        onDrop(e);
                    }}
                >
                    {icon}
                    <p className="text-sm text-gray-400 text-center">
                        Drag & Drop
                        <br />
                        <span className="text-themeColorAlternate">
                            or browse
                        </span>
                    </p>
                </label>
                <div className="block">
                    {files.map((image, index) => (
                        <div
                            key={index}
                            data-title={image.name}
                            data-testid="SELECTED_FILE"
                            className={`float-left m-1 relative border border-gray-300 rounded-lg ${imageSize}`}
                        >
                            {accept === 'image/*' ? (
                                <img
                                    src={getObjectURL(image)}
                                    className="rounded-lg border-white object-cover h-full w-full pointer-events-none"
                                />
                            ) : (
                                <object
                                    data={getObjectURL(image)}
                                    className="rounded-lg border-white object-cover h-full w-full pointer-events-none"
                                />
                            )}

                            <button
                                aria-label="close-information"
                                data-testid="REMOVE_FILE"
                                type="button"
                                className="text-gray-400 bg-white dark:bg-black app-button border border-blue-200 !rounded-full !p-0 text-sm !w-6 !h-6 flex justify-center items-center absolute -top-2 -right-2 z-50"
                                onClick={() => removeLocalImages(index)}
                            >
                                <SvgIcon
                                    path="/icons/svg/close.svg"
                                    className="w-5 h-5"
                                ></SvgIcon>
                            </button>
                        </div>
                    ))}
                </div>
            </div>
            <p
                data-testid="`${name}-error`"
                className={`mt-1 text-xs italic min-h-4 ${error || localError ? 'text-red-500' : 'text-gray-400'}`}
            >
                {localError || error || helperText}
            </p>
            <DialogView
                open={showCropper}
                close={toggleCropper}
                hideClose={true}
            >
                <CropperView
                    image={localFile}
                    close={toggleCropper}
                    callback={callback}
                />
            </DialogView>
        </div>
    );
};

import { ReactNode, KeyboardEvent } from 'react';

export interface IFormField {
    name?: string;
    label?: string;
    value?: ILargeRecord;
    error?: string;
    validations?: {
        type: 'regex' | 'function';
        message?: string;
        validate:
            | string
            | ((
                  values: ILargeRecord,
                  name: string
              ) => Promise<string> | string);
    }[];
    required?: boolean;
    requiredLabel?: string;
    type?:
        | 'label'
        | 'text'
        | 'textarea'
        | 'number'
        | 'date'
        | 'time'
        | 'datetime-local'
        | 'file'
        | 'tag'
        | 'password'
        | 'autocomplete'
        | 'select'
        | 'otp'
        | 'calendar'
        | 'checkbox'
        | 'chip'
        | 'multiTextField'
        | 'toggle'
        | 'radio'
        | 'custom';
    placeHolder?: string;
    helperText?: string;
    rows?: number;
    alignClass?: string;
    className?: string;
    layoutClass?: string;
    imageSize?: string;
    size?: string;
    disabled?: boolean;
    multiple?: boolean;
    min?: string;
    max?: string;
    accept?: string;
    format?: string;
    options?: { id: string; label: string }[];
    buttonText?: string;
    buttonSize?: string;
    ref?: string;
    initialFieldCount?: number;
    remove?: (index: number) => void;
    customField?: boolean;
    fileSize?: number;
    cropper?: boolean;
    length?: number;
    noError?: boolean;
    disableFilter?: boolean;
    maxLength?: number;

    //
    startIcon?: ReactNode;
    endIcon?: ReactNode;
    element?: ReactNode;
    icon?: ReactNode;
    onChange?: (field: IFieldChange) => void;
    onKeyPress?: (e: KeyboardEvent<HTMLInputElement>) => void;
}

export type IFieldChange = {
    name: string;
    value: ILargeRecord;
    ignoreValidation?: boolean;
    ref?: string;
    id?: boolean;
};

import { FormEvent, ReactNode, useEffect, useState } from 'react';

//
import { getTagValues } from 'services/constants';

//
import { IFieldChange, IFormField } from './form.types';
import { TextField } from './textField';
import { CheckBox } from './checkBox';
import { FileUpload } from './fileUpload';

//
export const FormBuilder = ({
    form,
    call,
    buttonText,
    buttonClass,
    layoutClass,
    formTop,
    formBottom,
}: {
    form: Record<string, IFormField>;
    call: (...args: ILargeRecord) => Promise<boolean>;
    buttonText?: string;
    buttonClass?: string;
    layoutClass?: string;
    formTop?: ReactNode;
    formBottom?: ReactNode;
}) => {
    const [data, setData] = useState<Record<string, IFormField>>({});
    const [initialData, setInitialData] = useState<Record<string, IFormField>>(
        {}
    );
    const [loading, setLoading] = useState(false);
    const [debounceTimer, setDebounceTimer] = useState(0);

    //

    const getPayload = () => {
        const payload: Record<string, ILargeRecord> = {};
        for (const key in data) {
            if (data[key].ref) {
                const refKey = data[key].ref;
                if (!payload[refKey]) payload[refKey] = [];
                payload[refKey].push(data[key].value);
            } else
                payload[key] =
                    data[key].type === 'tag'
                        ? getTagValues(data[key].value)
                        : data[key].value;
        }
        return payload;
    };

    const onFieldChange = async (field: IFieldChange) => {
        setData((_data) => {
            _data[field.name] = field.ref
                ? { ..._data[field.ref], ref: field.ref, value: field.value }
                : { ..._data[field.name], value: field.value };

            return _data;
        });

        if (!field.ignoreValidation) {
            if (debounceTimer) clearTimeout(debounceTimer);

            setDebounceTimer(
                setTimeout(() => {
                    const fieldData = data[field.name];
                    validateField(field.name, fieldData);
                }, 500)
            );
        }
    };

    const validateField = async (name: string, fieldData: IFormField) => {
        let errorMessage = '';
        const value = fieldData.value || '';

        if (
            fieldData.required &&
            (fieldData.type === 'tag'
                ? !value
                      .split(/( |;|,)/g)
                      .filter((t: string) =>
                          t.replaceAll(/(\s|;|,)/g, '').trim()
                      ).length
                : typeof value === 'object'
                  ? !value.length
                  : !`${value}`.trim())
        ) {
            errorMessage =
                fieldData.requiredLabel ||
                `${fieldData.label || 'field'} is required`;
        } else if (fieldData.validations) {
            for (const key in fieldData.validations) {
                const validation = fieldData.validations[key];
                const regex = new RegExp(validation.validate as string);
                if (validation.type === 'function') {
                    const payload = getPayload();
                    const call = validation.validate as (
                        values: ILargeRecord,
                        name: string
                    ) => Promise<string> | string;
                    errorMessage = await call(payload, name);
                } else if (
                    (validation.type === 'regex' &&
                        typeof value === 'object' &&
                        value.filter((v: ILargeRecord) => !regex.test(v))
                            .length) ||
                    (fieldData.type === 'tag'
                        ? value.split(/( |;|,)/g).filter((t: string) => {
                              const data = t.replaceAll(/(\s|;|,)/g, '').trim();
                              if (data) return !regex.test(data);
                              return false;
                          }).length
                        : !regex.test(value))
                ) {
                    errorMessage = validation.message || 'Invalid value';
                }
                if (errorMessage) break;
            }
        }
        setData((_data) => {
            const updatedData = { ..._data };
            updatedData[name] = {
                ...updatedData[name],
                error: errorMessage,
            };
            return updatedData;
        });
        return errorMessage;
    };

    const validate = async () => {
        const errors: Record<string, string> = {};
        for (const field in data) {
            if (
                data[field].type !== 'label' &&
                (data[field].type !== 'multiTextField' ||
                    (data[field].type === 'multiTextField' && data[field].ref))
            )
                errors[field] = await validateField(field, data[field]);
        }
        return !Object.values(errors).filter(Boolean).length;
    };

    const onSubmit = async (e: FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        setLoading(true);
        const payload = getPayload();
        if (await validate()) {
            const success = await call(payload);
            if (success) setData(initialData);
        }
        setLoading(false);
    };

    //
    useEffect(() => {
        setData({ ...form });
        setInitialData({ ...form });
    }, [form]);

    return (
        <form
            className={`grid gap-1 w-auto relative ${layoutClass}`}
            onSubmit={onSubmit}
        >
            {formTop}
            {Object.entries(data || {})
                .filter(([_, field]) => !field.ref)
                .map(([fieldName, field]) => (
                    <div
                        key={fieldName}
                        className={`w-full ${field.alignClass}`}
                    >
                        <FormElements
                            fieldName={fieldName}
                            field={field}
                            onFieldChange={onFieldChange}
                        />
                    </div>
                ))}
            {formBottom ? (
                formBottom
            ) : (
                <button
                    disabled={loading}
                    type="submit"
                    data-testid="SUBMIT"
                    className={`app-button ${loading ? 'text-gray-400' : ''} ${buttonClass}`}
                    onContextMenu={() => {
                        return false;
                    }}
                >
                    {buttonText}
                </button>
            )}
            {loading && (
                <div className="bg-gray-100 bg-opacity-50 dark:bg-transparent absolute w-full h-full rounded-lg flex items-center justify-center">
                    <div className="dot-pulse"></div>
                </div>
            )}
        </form>
    );
};

