import type { Request, Response } from 'express';
import type { ObjectSchema } from 'yup';

export type IErrorHandler = (
    error: IError,
    req: Request,
    res: Response,
    next: IErrorCallback
) => void;

export type IErrorCallback = (args?: Record<string, any>) => void;

export interface IError {
    stack: string;
    code: number;
    message: string | boolean | Record<string, string>;
}

export type IMiddleWare = (
    req: Request,
    res: Response,
    next: IErrorCallback,
    optional?: boolean
) => void;

export type IValidator = (validation: ObjectSchema<any>) => IMiddleWare;

export type IService<T> = (data: Record<string, any>) => Promise<T>;

import fs from 'fs';

//
import { generateId } from '../../src/utils';
import { createError } from './error';
import { IMiddleWare } from './middleware';
import { getKeysRepo } from '../../src/repository/security';

//
import messages from '../utils/messages';

//
(global as any).userKeys = {};

export const clientHandler: IMiddleWare = async (req, res, next) => {
    if (req.cookies.browserId) return next();

    const browserId = `BRWS-${generateId()}`;

    res.cookie('browserId', browserId, {
        httpOnly: true,
        secure: true,
        sameSite: 'strict',
        maxAge: +process.env.KEY_ROTATION_INTERVAL!,
    });

    next();
};

export const inputValidationMiddleware: IMiddleWare = (req, res, next) => {
    if (!['logger', 'error-logger'].includes(req.url)) {
        const keysToValidate = ['body', 'params', 'query'];

        for (const key of keysToValidate) {
            if (req[key as keyof typeof req]) {
                for (const [param, value] of Object.entries(
                    req[key as keyof typeof req]
                )) {
                    if (
                        typeof value === 'string' &&
                        !['password', 'newPassword', 'prompt'].includes(param)
                    ) {
                        const maliciousPattern =
                            /(<script.*?>.*?<\/script>|<.*?on\w+=".*?"|(?:\bSELECT\b|\bDROP\b|\bINSERT\b|\bDELETE\b|\bUPDATE\b).*?|\bUNION\b.*?\bSELECT\b|;--|<|>|\{|\}|[\\"']|\\x[0-9A-Fa-f]{2,}|\.\.|\/\*|\*\/)/gi;

                        if (maliciousPattern.test(value)) {
                            return next(
                                createError(
                                    400,
                                    messages.responses.harmfulContent
                                )
                            );
                        }
                    }
                }
            }
        }
    }
    next();
};

export const filePathScanner: IMiddleWare = async (req, res, next) => {
    const filePath = req.file?.path;

    if (!filePath)
        return next(createError(404, messages.responses.fileNotFound));

    fs.readFile(filePath, async (err, fileBuffer) => {
        const arrayBuffer = fileBuffer.buffer.slice(
            fileBuffer.byteOffset,
            fileBuffer.byteOffset + fileBuffer.byteLength
        );

        const hashBuffer = await crypto.subtle.digest('SHA-256', arrayBuffer);

        const hashArray = Array.from(new Uint8Array(hashBuffer));
        const hashHex = hashArray
            .map((byte) => byte.toString(16).padStart(2, '0'))
            .join('');
        if (req.body.checkSum !== hashHex)
            return next(createError(400, messages.responses.harmfulFile));

        next();
    });
};

export const getPrivateKey = async (clientId: string) => {
    if (!(global as any).userKeys[clientId]) {
        const { publicKey, privateKey, createdAt } =
            await getKeysRepo(clientId);
        (global as any).userKeys[clientId] = {
            publicKey: Uint8Array.from(Buffer.from(publicKey, 'base64')),
            privateKey: Uint8Array.from(Buffer.from(privateKey, 'base64')),
            createdAt: +createdAt,
        };
    }

    const privateKey = (global as any).userKeys[clientId]?.privateKey;

    if (!privateKey) return;

    const privateKeyObject = await crypto.subtle.importKey(
        'pkcs8',
        privateKey,
        { name: 'RSA-OAEP', hash: 'SHA-256' },
        false,
        ['decrypt']
    );

    return privateKeyObject;
};

export const decryptPayload: IMiddleWare = async (req, _, next) => {
    try {
        if (
            req.method === 'GET' ||
            req.headers['content-type']?.includes('multipart/form-data')
        )
            return next();

        const clientId = req.cookies.clientId;
        const privateKey = await getPrivateKey(clientId);

        if (
            !clientId ||
            !privateKey ||
            Date.now() - (global as any).userKeys[clientId]?.createdAt >
                +process.env.KEY_ROTATION_INTERVAL!
        ) {
            return next(createError(403, messages.responses.keyExpired));
        }

        const { encryptedSymmetricKey, encryptedData, iv } = req.body;

        if (!encryptedSymmetricKey || !encryptedData || !iv)
            return next(createError(403, messages.responses.keyExpired));

        const symmetricKeyRaw = await crypto.subtle.decrypt(
            { name: 'RSA-OAEP' },
            privateKey,
            Uint8Array.from(Buffer.from(encryptedSymmetricKey, 'base64'))
        );

        const symmetricKey = await crypto.subtle.importKey(
            'raw',
            symmetricKeyRaw,
            { name: 'AES-GCM' },
            false,
            ['decrypt']
        );

        const decryptedData = await crypto.subtle.decrypt(
            {
                name: 'AES-GCM',
                iv: Uint8Array.from(Buffer.from(iv, 'base64')),
            },
            symmetricKey,
            Uint8Array.from(Buffer.from(encryptedData, 'base64'))
        );

        req.body = JSON.parse(new TextDecoder().decode(decryptedData));
        next();
    } catch {
        next(createError(403, messages.responses.keyExpired));
    }
};

import { Response } from 'express';

//
import { IMiddleWare } from '../../src/handler/middleware';
// import { getAuth } from '../../src/libraries/firebase';
import { generateToken, verifyToken } from '../../src/libraries/tokenGenerator';
import { createError } from '../../src/handler/error';
import { getUserByIDandSecretKeyRepo } from '../../src/repository/user';

//
import messages from '../utils/messages';

//
const clearCookies = (res: Response) => {
    res.cookie('refreshToken', '', {
        httpOnly: true,
        secure: true,
        sameSite: 'strict',
    });
    res.cookie('accessToken', '', {
        httpOnly: true,
        secure: true,
        sameSite: 'strict',
    });
};

export const tokenChecker: IMiddleWare = async (
    req,
    res,
    next,
    optional = false
) => {
    try {
        req.body.userId = req.cookies.browserId;

        const refreshToken = req.cookies.refreshToken;
        const accessToken = req.cookies.accessToken;

        if (!optional && !refreshToken && !accessToken) {
            clearCookies(res);
            return next(createError(401, messages.responses.unauthorized));
        }

        let result;
        try {
            result = await verifyToken(accessToken);
        } catch {
            if (refreshToken) {
                result = await verifyToken(refreshToken);

                const payload = JSON.parse(atob(accessToken.split('.')?.[1]));

                const now = Date.now();

                if (
                    now - payload.lastUsed <=
                    +process.env.REFRESH_TOKEN_EXPIRES_IN!
                ) {
                    const newAccessTokenToken = await generateToken(
                        { ...result, lastUsed: now },
                        true
                    );
                    res.cookie('accessToken', newAccessTokenToken, {
                        httpOnly: true,
                        secure: true,
                        sameSite: 'strict',
                        maxAge: +process.env.REFRESH_TOKEN_EXPIRES_IN!,
                    });
                } else {
                    clearCookies(res);
                    return next(
                        createError(401, messages.responses.unauthorized)
                    );
                }
            }
        }

        const {
            iat: _,
            exp: __,
            lastUsed: ___,
            ...data
        } = result as Record<string, string>;

        if (data.secretKey) {
            const isUserAvailable = await getUserByIDandSecretKeyRepo(
                data.userId,
                data.secretKey
            );

            if (!isUserAvailable) {
                clearCookies(res);
                return next(createError(401, messages.responses.unauthorized));
            }
        }

        req.body = {
            ...req.body,
            ...data,
        };

        next();
    } catch {
        if (optional) return next();
        else {
            clearCookies(res);
            return next(createError(401, messages.responses.unauthorized));
        }
    }
};

//
export const headerTokenChecker: IMiddleWare = async (req, _, next) => {
    try {
        const token = req.headers.authorization?.split(' ')?.[1];
        if (!token) throw createError(401, messages.responses.unauthorized);

        const result = await verifyToken(token);

        req.body = {
            ...result,
            ...req.body,
        };
        next();
    } catch {
        return next(createError(401, messages.responses.unauthorized));
    }
};

