export const encrypt = async (
    data: Record<string, string>,
    storedPublicKey: string
) => {
    if (storedPublicKey) {
        const binaryString = atob(storedPublicKey);
        const binaryData = Uint8Array.from(binaryString, (char) =>
            char.charCodeAt(0)
        );
        const publicKey = await crypto.subtle.importKey(
            'spki',
            binaryData,
            { name: 'RSA-OAEP', hash: 'SHA-256' },
            false,
            ['encrypt']
        );

        const symmetricKey = await crypto.subtle.generateKey(
            { name: 'AES-GCM', length: 256 },
            true,
            ['encrypt', 'decrypt']
        );

        const symmetricKeyRaw = await crypto.subtle.exportKey(
            'raw',
            symmetricKey
        );

        const encryptedSymmetricKey = await crypto.subtle.encrypt(
            { name: 'RSA-OAEP' },
            publicKey,
            symmetricKeyRaw
        );

        const encoder = new TextEncoder();
        const encodedData = encoder.encode(JSON.stringify(data));
        const iv = crypto.getRandomValues(new Uint8Array(12));
        const encryptedData = await crypto.subtle.encrypt(
            { name: 'AES-GCM', iv },
            symmetricKey,
            encodedData
        );

        const encryptedSymmetricKeyBase64 = btoa(
            String.fromCharCode(...new Uint8Array(encryptedSymmetricKey))
        );
        const encryptedDataBase64 = btoa(
            String.fromCharCode(...new Uint8Array(encryptedData))
        );
        const ivBase64 = btoa(String.fromCharCode(...iv));

        return {
            encryptedSymmetricKey: encryptedSymmetricKeyBase64,
            encryptedData: encryptedDataBase64,
            iv: ivBase64,
        };
    }
    return data;
};

import { Plugin } from 'vite';
import { readFileSync, readdirSync, statSync } from 'fs';
import { resolve, basename, join } from 'path';

const getAllSvgFiles = (dir: string): Record<string, string> => {
    const svgMap: Record<string, string> = {};

    function readDirectory(directory: string) {
        readdirSync(directory).forEach((file) => {
            const filePath = resolve(directory, file);
            const stat = statSync(filePath);

            if (stat.isDirectory()) {
                // Recursively read subdirectory
                readDirectory(filePath);
            } else if (file.endsWith('.svg')) {
                // Add SVG file to the map
                const svgContent = readFileSync(filePath, 'utf-8');
                const fileName = join(
                    basename(directory),
                    basename(file)
                ).replace(/\\/g, '/');
                svgMap[fileName] = svgContent;
            }
        });
    }

    readDirectory(dir);
    return svgMap;
};

export const svgIconsPlugin = (svgDir: string): Plugin => {
    const svgMap = getAllSvgFiles(svgDir);

    return {
        name: 'vite-plugin-svg-icons',
        resolveId(id) {
            if (id === 'virtual:svg-icons') {
                return id;
            }
        },
        load(id) {
            if (id === 'virtual:svg-icons') {
                return `
					export function getSvgIcon(name) {
						return ${JSON.stringify(svgMap)}[name];
					}
				`;
            }
        },
    };
};

