    requiredLabel?: string;
    type?:
        | 'label'
        | 'text'
        | 'textarea'
        | 'number'
        | 'date'
        | 'time'
        | 'datetime-local'
        | 'file'
        | 'tag'
        | 'password'
        | 'autocomplete'
        | 'select'
        | 'otp'
        | 'calendar'
        | 'checkbox'
        | 'chip'
        | 'multiTextField'
        | 'toggle'
        | 'radio'
        | 'custom';
