import mysql, { Pool, PoolOptions } from 'mysql2/promise';

export class MYSQLConnection {
    private static pool: Pool;
    private static logspool: Pool;
    private static keyspool: Pool;
    private constructor() {}

    static configurePool(): Pool {
        if (!MYSQLConnection.pool) {
            const poolOptions: PoolOptions = {
                connectionLimit: 150,
                host: process.env.DB_HOST,
                port: parseInt(process.env.DATABASE_PORT!),
                user: process.env.DB_USER,
                password: process.env.DB_PASSWORD,
                database: process.env.DATABASE_NAME,
                debug: false,
                multipleStatements: false,
                charset: 'utf8mb4',
            };
            MYSQLConnection.pool = mysql.createPool(poolOptions);

            console.log('MySQL pool connection configured.');
        }

        return MYSQLConnection.pool;
    }

    static configureLogsPool(): Pool {
        if (!MYSQLConnection.logspool) {
            const poolOptions: PoolOptions = {
                connectionLimit: 150,
                host: process.env.DB_HOST,
                port: parseInt(process.env.DATABASE_PORT!),
                user: process.env.DB_USER,
                password: process.env.DB_PASSWORD,
                database: process.env.LOG_DATABASE_NAME,
                debug: false,
                multipleStatements: false,
                charset: 'utf8mb4',
            };
            MYSQLConnection.logspool = mysql.createPool(poolOptions);

            console.log('MySQL Logs pool connection configured.');
        }

        return MYSQLConnection.logspool;
    }

    static configureKeysPool(): Pool {
        if (!MYSQLConnection.keyspool) {
            const poolOptions: PoolOptions = {
                connectionLimit: 150,
                host: process.env.DB_HOST,
                port: parseInt(process.env.DATABASE_PORT!),
                user: process.env.DB_USER,
                password: process.env.DB_PASSWORD,
                database: process.env.KEY_DATABASE_NAME,
                debug: false,
                multipleStatements: false,
                charset: 'utf8mb4',
            };
            MYSQLConnection.keyspool = mysql.createPool(poolOptions);

            console.log('MySQL Logs pool connection configured.');
        }

        return MYSQLConnection.keyspool;
    }

    static closePool() {
        if (MYSQLConnection.pool) {
            MYSQLConnection.pool.end();
            console.log('MySQL pool connection closed.');
        }
        if (MYSQLConnection.logspool) {
            MYSQLConnection.logspool.end();
            console.log('MySQL pool connection closed.');
        }
        if (MYSQLConnection.keyspool) {
            MYSQLConnection.keyspool.end();
            console.log('MySQL pool connection closed.');
        }
    }
}

export const executeQuery: IQuery = async (
    query: string,
    values?: any
): Promise<any> => {
    const pool = MYSQLConnection.configurePool();
    const [results] = await pool.query(query, values);
    return results;
};

export const executeLogsQuery: IQuery = async (
    query: string,
    values?: any
): Promise<any> => {
    const pool = MYSQLConnection.configureLogsPool();
    const [results] = await pool.query(query, values);
    return results;
};

export const executeKeysQuery: IQuery = async (
    query: string,
    values?: any
): Promise<any> => {
    const pool = MYSQLConnection.configureKeysPool();
    const [results] = await pool.query(query, values);
    return results;
};

export type IQuery = (query: string, values?: any) => Promise<any>;

import { logErrorToFile } from '../../src/handler/logger';
import { IErrorHandler } from '../../src/handler/middleware';

//
import messages from '../../src/utils/messages';

//
export const createError = (code: number, message: string) => {
    const error = new Error(message);
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    (error as any).code = code;
    return error;
};

export const errorHandler: IErrorHandler = (error, _req, res, _next) => {
    if (!Object.values(messages.responses).includes(`${error.message}`))
        logErrorToFile(error);

    const statusCode =
        typeof error.code === 'string' ? parseInt(error.code, 10) : error.code;
    const validStatusCode = isNaN(statusCode) ? 500 : statusCode;
    const validateMessage =
        typeof error.code === 'string' ? 'Something went wrong' : error.message;

    res.status(validStatusCode).send({
        message: validateMessage,
    });
};

import fs from 'fs';
import path from 'path';
import { IError, IMiddleWare } from '../../src/handler/middleware';

// Function to get the current date in YYYY-MM-DD format
const getDateString = () => {
    return new Date().toISOString().split('T')[0];
};

// Error log file with date
const getErrorLogFilePath = () =>
    path.join(
        __dirname,
        `${process.env.MAIN_PATH}/logs`,
        `error-${getDateString()}.log`
    );

export const logErrorToFile = (error: IError) => {
    const logFilePath = getErrorLogFilePath();
    const logMessage = `[${new Date().toISOString()}] ${error.stack || error.message}\n`;
    if (!fs.existsSync(path.dirname(logFilePath))) {
        fs.mkdirSync(path.dirname(logFilePath), { recursive: true });
    }
    fs.appendFileSync(logFilePath, logMessage, 'utf8');
};

// Access log file with date
const getAccessLogFilePath = () =>
    path.join(
        __dirname,
        `${process.env.MAIN_PATH}/logs`,
        `access-${getDateString()}.log`
    );

export const logAccess: IMiddleWare = (req, res, next) => {
    const logFilePath = getAccessLogFilePath();
    const logMessage = `[${new Date().toISOString()}] ${req.method} ${req.url} ${req.ip} params: ${JSON.stringify(req.params || {})}, body: ${JSON.stringify(req.body || {})}\n`;
    if (!fs.existsSync(path.dirname(logFilePath))) {
        fs.mkdirSync(path.dirname(logFilePath), { recursive: true });
    }
    fs.appendFileSync(logFilePath, logMessage, 'utf8');
    next();
};

// Mail log file with date
const getMailLogFilePath = () =>
    path.join(
        __dirname,
        `${process.env.MAIN_PATH}/logs`,
        `mail-${getDateString()}.log`
    );

// eslint-disable-next-line @typescript-eslint/no-explicit-any
export const logMail = (data: Record<string, any>) => {
    const logFilePath = getMailLogFilePath();
    const logMessage = `[${new Date().toISOString()}] Config: ${JSON.stringify(data.config)}, Error: ${JSON.stringify(data.error)}, Result: ${JSON.stringify(data.data || {})}\n`;
    if (!fs.existsSync(path.dirname(logFilePath))) {
        fs.mkdirSync(path.dirname(logFilePath), { recursive: true });
    }
    fs.appendFileSync(logFilePath, logMessage, 'utf8');
};

