        [JSON.stringify(user)]
    );

    const call = async (_payload: ILargeRecord) => {
        const res = await updateUser({
            name: _payload.name,
            profileURL: _payload.profileURL,
        });
        if (!res.error) user?.updateUser?.();
        return !res.error;
    };

    return (
        <>
            <p
                className="text-lg font-bold flex items-center gap-2 border-b pb-4"
                data-testid="USERINFO_PAGE"
            >
                <SvgIcon path="/icons/svg/user.svg" className="w-5 h-5" />{' '}
                Profile
            </p>
            <p className="text-md text-gray-400">
                Update your personal information and profile settings.
            </p>
            <FormBuilder
                form={form}
                call={call}
                buttonText="Update"
                layoutClass={`gap-1 sm:gap-2 max-w-screen-sm mt-6`}
                buttonClass={`app-button app-shadow !text-[16px] !px-6`}
            />
        </>
    );
};

// ./demos/codeBlock.tsx
import { useState } from 'react';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { darcula } from 'react-syntax-highlighter/dist/esm/styles/prism';

export const CodeBlock = ({ code }: { code: string }) => {
    const [isExpanded, setIsExpanded] = useState(false);
    const [isCopied, setIsCopied] = useState(false);

    // Truncate code to first 5 lines for collapsed view
    const truncatedCode = code.split('\n').slice(0, 5).join('\n');
    const displayCode = isExpanded ? code : truncatedCode;

    const handleCopy = () => {
        navigator.clipboard.writeText(code).then(() => {
            setIsCopied(true);
            setTimeout(() => setIsCopied(false), 2000); // Reset after 2 seconds
        });
    };

    const handleToggle = () => {
        setIsExpanded(!isExpanded);
    };

    return (
        <div className="w-full rounded-xl p-3 bg-neutral-800 max-w-screen-md mx-auto no-scrollbar relative">
            <button
                onClick={handleCopy}
                className="absolute top-3 right-3 px-2 py-1 text-sm text-white bg-neutral-700 hover:bg-neutral-600 rounded-md transition"
                title={isCopied ? 'Copied!' : 'Copy to clipboard'}
            >
                {isCopied ? 'Copied!' : 'Copy'}
            </button>

            <SyntaxHighlighter
                language="tsx"
                style={darcula}
                customStyle={{
                    margin: 0,
                    padding: '1rem',
                    background: 'transparent',
                    maxHeight: isExpanded ? 'none' : '200px',
                    overflowY: 'hidden',
                }}
            >
                {displayCode}
            </SyntaxHighlighter>

            {code.split('\n').length > 5 && (
                <button
                    onClick={handleToggle}
                    className="mt-2 text-sm text-neutral-300 hover:text-white underline"
                >
                    {isExpanded ? 'Collapse' : 'Expand'}
                </button>
            )}
        </div>
    );
};

